document.addEventListener("DOMContentLoaded",function(){const imgs=document.querySelectorAll("img.lazy");
const lazyLoad=img=>{const src=img.getAttribute("data-src");
if(!src)return;img.src=src;img.classList.remove("lazy")};
if("IntersectionObserver"in window){const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){lazyLoad(entry.target);
    
    observer.unobserve(entry.target)}})});imgs.forEach(img=>observer.observe(img))}else{imgs.forEach(img=>lazyLoad(img))}});
